mapper.select:
select * from Company where com_status='y'
and com_name like concat('%', #{cri.searchTitle}, '%')
ORDER BY c_no desc limit #{cri.pageStart}, #{cri.amount}

select * from Company where com_status='n'
and com_name like concat('%', #{cri.searchTitle}, '%')
ORDER BY c_no desc limit #{cri.pageStart}, #{cri.amount}

mapper.update:
		update company 
		set com_status = 'y', com_registration_date = now()
    where com_id = #{com_id}
