mapper.select:
        SELECT * FROM employment_qa
        WHERE user_id = #{user_id}

				   and qa_title like concat('%', #{cri.searchTitle}, '%')


				   and qa_content like concat('%', #{cri.searchContent}, '%')

        ORDER BY qa_no DESC
        LIMIT #{cri.pageStart}, #{cri.amount}

        SELECT * FROM employment_qa
        WHERE com_id = #{com_id}

				   and qa_title like concat('%', #{cri.searchTitle}, '%')


				   and qa_content like concat('%', #{cri.searchContent}, '%')

        ORDER BY qa_no DESC
        LIMIT #{cri.pageStart}, #{cri.amount}

select count(*) as total from employment_qa where user_id = #{user_id}
and qa_title like concat('%', #{cri.searchTitle}, '%')
and qa_content like concat('%', #{cri.searchContent}, '%')

select count(*) as total from employment_qa where com_id = #{com_id}
and qa_title like concat('%', #{cri.searchTitle}, '%')
and qa_content like concat('%', #{cri.searchContent}, '%')
