mapper.update:
update resume_write set w_address = #{w_address}, w_level = #{w_level}, w_finish = #{w_finish},
w_major = #{w_major}, w_fndate = #{w_fndate}, w_com = #{w_com},
w_position = #{w_position}, w_join = #{w_join}, w_leave = #{w_leave},
w_subject = #{w_subject}, w_score = #{w_score}, w_get = #{w_get},
w_license = #{w_license}, w_getlicense = #{w_getlicense}
where w_no = #{w_no};

mapper.select:
select * from resume_write

order by w_no desc
limit #{cri.pageStart}, #{cri.amount}
